import React, { useState, useEffect } from 'react';
import { Box, Typography, TextField, Grid, Card, CardContent, CardMedia, IconButton } from '@mui/material';
import { PlayArrow, Pause } from '@mui/icons-material';
import { spotifyApi } from '../services/spotify';

function MainView({ onTrackSelect }) {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [playlists, setPlaylists] = useState([]);
  const [currentTrack, setCurrentTrack] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);

  useEffect(() => {
    const loadPlaylists = async () => {
      try {
        const data = await spotifyApi.getUserPlaylists();
        setPlaylists(data.items);
      } catch (error) {
        console.error('Error loading playlists:', error);
        if (error.response?.status === 401) {
          // Token expired, redirect to login
          window.localStorage.removeItem("token");
          window.location.reload();
        }
      }
    };

    loadPlaylists();
  }, []);

  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      const data = await spotifyApi.search(searchQuery);
      setSearchResults(data.tracks.items);
    } catch (error) {
      console.error('Error searching:', error);
    }
  };

  const handlePlay = async (track) => {
    try {
      await spotifyApi.play(track.uri);
      setCurrentTrack(track);
      setIsPlaying(true);
    } catch (error) {
      console.error('Error playing track:', error);
    }
  };

  return (
    <Box sx={{ padding: '2rem', color: 'white' }}>
      <form onSubmit={handleSearch}>
        <TextField
          fullWidth
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          placeholder="Search for songs, artists, or albums"
          sx={{
            marginBottom: '2rem',
            '& .MuiInputBase-input': {
              color: 'white',
            },
            '& .MuiOutlinedInput-root': {
              '& fieldset': {
                borderColor: 'rgba(255, 255, 255, 0.3)',
              },
              '&:hover fieldset': {
                borderColor: 'rgba(255, 255, 255, 0.5)',
              },
            },
          }}
        />
      </form>

      {searchResults.length > 0 && (
        <Box sx={{ marginBottom: '2rem' }}>
          <Typography variant="h5" sx={{ marginBottom: '1rem' }}>
            Search Results
          </Typography>
          <Grid container spacing={2}>
            {searchResults.map((track) => (
              <Grid item xs={12} sm={6} md={4} key={track.id}>
                <Card sx={{ display: 'flex', bgcolor: 'rgba(0, 0, 0, 0.5)' }}>
                  <CardMedia
                    component="img"
                    sx={{ width: 100 }}
                    image={track.album.images[0]?.url}
                    alt={track.name}
                  />
                  <CardContent sx={{ flex: '1', color: 'white' }}>
                    <Typography variant="subtitle1">{track.name}</Typography>
                    <Typography variant="body2">{track.artists[0].name}</Typography>
                    <IconButton
                      onClick={() => handlePlay(track)}
                      sx={{ color: 'white' }}
                    >
                      {currentTrack?.id === track.id && isPlaying ? <Pause /> : <PlayArrow />}
                    </IconButton>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Box>
      )}

      <Box>
        <Typography variant="h5" sx={{ marginBottom: '1rem' }}>
          Your Playlists
        </Typography>
        <Grid container spacing={2}>
          {playlists.map((playlist) => (
            <Grid item xs={12} sm={6} md={4} key={playlist.id}>
              <Card sx={{ bgcolor: 'rgba(0, 0, 0, 0.5)' }}>
                <CardMedia
                  component="img"
                  height="160"
                  image={playlist.images[0]?.url}
                  alt={playlist.name}
                />
                <CardContent sx={{ color: 'white' }}>
                  <Typography variant="subtitle1">{playlist.name}</Typography>
                  <Typography variant="body2">{playlist.tracks.total} tracks</Typography>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Box>
    </Box>
  );
}

export default MainView;
