import axios from 'axios';

const SPOTIFY_BASE_URL = 'https://api.spotify.com/v1';
const CLIENT_ID = "aa8f7326ca5b4e3d82f69335808fa61e";
const REDIRECT_URI = "http://127.0.0.1:300";

export const spotifyApi = {
  token: null,

  setAccessToken(token) {
    this.token = token;
    axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
  },

  getHeaders() {
    return {
      'Authorization': `Bearer ${this.token}`,
      'Content-Type': 'application/json',
    };
  },

  async getAccessToken(code) {
    try {
      const response = await axios.post('https://accounts.spotify.com/api/token', null, {
        params: {
          grant_type: 'authorization_code',
          code,
          redirect_uri: REDIRECT_URI,
          client_id: CLIENT_ID
        },
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded'
        }
      });
      return response.data.access_token;
    } catch (error) {
      console.error('Error getting access token:', error);
      throw error;
    }
  },

  async search(query, types = ['track', 'artist', 'album']) {
    try {
      const response = await axios.get(`${SPOTIFY_BASE_URL}/search`, {
        headers: this.getHeaders(),
        params: {
          q: query,
          type: types.join(','),
          limit: 20
        }
      });
      return response.data;
    } catch (error) {
      this.handleError(error);
      throw error;
    }
  },

  async getUserPlaylists() {
    try {
      const response = await axios.get(`${SPOTIFY_BASE_URL}/me/playlists`, {
        headers: this.getHeaders()
      });
      return response.data;
    } catch (error) {
      this.handleError(error);
      throw error;
    }
  },

  async getPlaylist(playlistId) {
    const response = await axios.get(`${SPOTIFY_BASE_URL}/playlists/${playlistId}`, {
      headers: {
        'Authorization': `Bearer ${this.token}`
      }
    });
    return response.data;
  },

  async play(uri) {
    try {
      await axios.put(`${SPOTIFY_BASE_URL}/me/player/play`, {
        uris: [uri]
      }, {
        headers: this.getHeaders()
      });
    } catch (error) {
      this.handleError(error);
      throw error;
    }
  },

  async pause() {
    try {
      await axios.put(`${SPOTIFY_BASE_URL}/me/player/pause`, {}, {
        headers: this.getHeaders()
      });
    } catch (error) {
      this.handleError(error);
      throw error;
    }
  },

  handleError(error) {
    if (error.response?.status === 401) {
      window.localStorage.removeItem("token");
      window.location.href = '/';
    }
  }
};
