import React from 'react';
import styled from 'styled-components';

const LoginContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  background-color: black;
  gap: 2rem;
`;

const LoginButton = styled.a`
  padding: 1rem 5rem;
  background-color: #1db954;
  border-radius: 5rem;
  border: none;
  color: white;
  font-weight: bold;
  font-size: 1.2rem;
  cursor: pointer;
  text-decoration: none;

  &:hover {
    background-color: #1ed760;
  }
`;

const Title = styled.h1`
  color: #1db954;
  font-size: 2.5rem;
  margin-bottom: 2rem;
`;

function Login() {
  const CLIENT_ID = "YOUR_CLIENT_ID"; // Replace with your Spotify Client ID
  const REDIRECT_URI = "http://localhost:3000";
  const AUTH_ENDPOINT = "https://accounts.spotify.com/authorize";
  const RESPONSE_TYPE = "token";
  const SCOPE = "streaming user-read-email user-read-private user-library-read user-library-modify user-read-playback-state user-modify-playback-state";

  const loginUrl = `${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}&scope=${SCOPE}`;

  return (
    <LoginContainer>
      <Title>Spotify Clone</Title>
      <LoginButton href={loginUrl}>Login with Spotify</LoginButton>
    </LoginContainer>
  );
}

export default Login;
